"use client";

import { useState, useEffect } from "react";
import { useRouter, useParams } from "next/navigation";
import { ArrowLeft, Save, Mail, User, Shield, Trash2 } from "lucide-react";
import { Button } from "@/components/ui/Button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/Card";
import { toast } from "sonner";

export default function EditCommitteeMemberPage() {
    const router = useRouter();
    const params = useParams();
    const [isLoading, setIsLoading] = useState(false);

    // Mock Data Loading
    const [formData, setFormData] = useState({
        name: "Dr. Anjali Sharma",
        email: "anjali.s@college.edu",
        role: "Chairperson",
        department: "Sociology"
    });

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setIsLoading(true);

        // Mock API call
        setTimeout(() => {
            toast.success("Member details updated!");
            setIsLoading(false);
            router.push("/admin/committee");
        }, 800);
    };

    return (
        <div className="max-w-2xl mx-auto space-y-6">
            <Button variant="ghost" className="mb-4" onClick={() => router.back()}>
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Committee
            </Button>

            <div className="flex justify-between items-center">
                <h1 className="text-2xl font-bold text-gray-900">Edit Member</h1>
                <Button variant="destructive" size="sm" onClick={() => toast.success("Member removed (Mock)")}>
                    <Trash2 className="w-4 h-4 mr-2" />
                    Remove Member
                </Button>
            </div>

            <Card>
                <CardHeader>
                    <CardTitle>Member Details</CardTitle>
                </CardHeader>
                <CardContent>
                    <form onSubmit={handleSubmit} className="space-y-4">
                        <div className="space-y-2">
                            <label className="text-sm font-medium text-gray-700 flex items-center">
                                <User className="w-4 h-4 mr-2" />
                                Full Name
                            </label>
                            <input
                                required
                                type="text"
                                value={formData.name}
                                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                            />
                        </div>

                        <div className="space-y-2">
                            <label className="text-sm font-medium text-gray-700 flex items-center">
                                <Mail className="w-4 h-4 mr-2" />
                                Email Address
                            </label>
                            <input
                                required
                                type="email"
                                value={formData.email}
                                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                            />
                        </div>

                        <div className="grid grid-cols-2 gap-4">
                            <div className="space-y-2">
                                <label className="text-sm font-medium text-gray-700 flex items-center">
                                    <Shield className="w-4 h-4 mr-2" />
                                    Role
                                </label>
                                <select
                                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                                    value={formData.role}
                                    onChange={(e) => setFormData({ ...formData, role: e.target.value })}
                                >
                                    <option value="Member">Member</option>
                                    <option value="Chairperson">Chairperson</option>
                                    <option value="Secretary">Secretary</option>
                                    <option value="Counselor">Counselor</option>
                                </select>
                            </div>

                            <div className="space-y-2">
                                <label className="text-sm font-medium text-gray-700">Department</label>
                                <input
                                    type="text"
                                    value={formData.department}
                                    onChange={(e) => setFormData({ ...formData, department: e.target.value })}
                                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                                />
                            </div>
                        </div>

                        <div className="pt-4 flex justify-end">
                            <Button type="submit" size="lg" disabled={isLoading}>
                                {isLoading ? "Saving..." : "Save Changes"}
                            </Button>
                        </div>
                    </form>
                </CardContent>
            </Card>
        </div>
    );
}

"use client";

import { useState } from "react";
import { useRouter } from "next/navigation";
import { ArrowLeft, Save, Mail, User, Shield } from "lucide-react";
import { Button } from "@/components/ui/Button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/Card";
import { toast } from "sonner";

export default function AddCommitteeMemberPage() {
    const router = useRouter();
    const [isLoading, setIsLoading] = useState(false);

    // Form state
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        role: "Member",
        department: ""
    });

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setIsLoading(true);

        // Mock API call
        setTimeout(() => {
            toast.success("Invitation sent successfully!");
            setIsLoading(false);
            router.push("/admin/committee");
        }, 1000);
    };

    return (
        <div className="max-w-2xl mx-auto space-y-6">
            <Button variant="ghost" className="mb-4" onClick={() => router.back()}>
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Committee
            </Button>

            <h1 className="text-2xl font-bold text-gray-900">Add Committee Member</h1>

            <Card>
                <CardHeader>
                    <CardTitle>Member Details</CardTitle>
                </CardHeader>
                <CardContent>
                    <form onSubmit={handleSubmit} className="space-y-4">
                        <div className="space-y-2">
                            <label className="text-sm font-medium text-gray-700 flex items-center">
                                <User className="w-4 h-4 mr-2" />
                                Full Name
                            </label>
                            <input
                                required
                                type="text"
                                placeholder="e.g. Dr. Anjali Sharma"
                                value={formData.name}
                                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                            />
                        </div>

                        <div className="space-y-2">
                            <label className="text-sm font-medium text-gray-700 flex items-center">
                                <Mail className="w-4 h-4 mr-2" />
                                Email Address
                            </label>
                            <input
                                required
                                type="email"
                                placeholder="name@college.edu"
                                value={formData.email}
                                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                            />
                        </div>

                        <div className="grid grid-cols-2 gap-4">
                            <div className="space-y-2">
                                <label className="text-sm font-medium text-gray-700 flex items-center">
                                    <Shield className="w-4 h-4 mr-2" />
                                    Role
                                </label>
                                <select
                                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                                    value={formData.role}
                                    onChange={(e) => setFormData({ ...formData, role: e.target.value })}
                                >
                                    <option value="Member">Member</option>
                                    <option value="Chairperson">Chairperson</option>
                                    <option value="Secretary">Secretary</option>
                                    <option value="Counselor">Counselor</option>
                                </select>
                            </div>

                            <div className="space-y-2">
                                <label className="text-sm font-medium text-gray-700">Department</label>
                                <input
                                    type="text"
                                    placeholder="e.g. Psychology"
                                    value={formData.department}
                                    onChange={(e) => setFormData({ ...formData, department: e.target.value })}
                                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
                                />
                            </div>
                        </div>

                        <div className="pt-4 flex justify-end">
                            <Button type="submit" size="lg" disabled={isLoading}>
                                {isLoading ? "Sending Invitation..." : "Send Invitation"}
                            </Button>
                        </div>
                    </form>
                </CardContent>
            </Card>
        </div>
    );
}

"use client";

import { useEffect, useState } from "react";
import { useRouter } from "next/navigation";
import { Users, UserPlus, Mail, Shield } from "lucide-react";
import { Button } from "@/components/ui/Button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/Card";
import { toast } from "sonner";

export default function AdminCommitteePage() {
    const router = useRouter();
    // Placeholder state - normally fetched from API
    const [members, setMembers] = useState([
        { id: 1, name: "Dr. Anjali Sharma", role: "Chairperson", email: "anjali.s@college.edu", status: "Active" },
        { id: 2, name: "Prof. Rajesh Kumar", role: "Member", email: "rajesh.k@college.edu", status: "Active" },
        { id: 3, name: "Mrs. Sunita Verma", role: "Counselor", email: "sunita.v@college.edu", status: "On Leave" },
    ]);

    const handleAddMember = () => {
        toast.info("Member invitation system coming soon!");
    };

    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center">
                <div>
                    <h1 className="text-2xl font-bold text-gray-900">Committee Management</h1>
                    <p className="text-gray-600">Manage members of your Internal Complaints Committee (ICC)</p>
                </div>
                <Button onClick={() => router.push("/admin/committee/add")}>
                    <UserPlus className="w-4 h-4 mr-2" />
                    Add Member
                </Button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {members.map((member) => (
                    <Card key={member.id} className="hover:shadow-md transition-shadow">
                        <CardContent className="pt-6">
                            <div className="flex items-start justify-between">
                                <div className="flex items-center space-x-4">
                                    <div className="w-12 h-12 rounded-full bg-blue-100 flex items-center justify-center text-blue-600 font-bold text-lg">
                                        {member.name.charAt(0)}
                                    </div>
                                    <div>
                                        <h3 className="font-semibold text-gray-900">{member.name}</h3>
                                        <p className="text-sm text-blue-600 font-medium">{member.role}</p>
                                    </div>
                                </div>
                                <span className={`px-2 py-1 rounded text-xs font-medium ${member.status === "Active" ? "bg-green-100 text-green-700" : "bg-yellow-100 text-yellow-700"
                                    }`}>
                                    {member.status}
                                </span>
                            </div>

                            <div className="mt-6 space-y-3">
                                <div className="flex items-center text-sm text-gray-500">
                                    <Mail className="w-4 h-4 mr-2" />
                                    {member.email}
                                </div>
                                <div className="flex items-center text-sm text-gray-500">
                                    <Shield className="w-4 h-4 mr-2" />
                                    Committee Access
                                </div>
                            </div>

                            <div className="mt-6 pt-4 border-t border-gray-100 flex justify-end gap-2">
                                <Button variant="ghost" size="sm" className="text-gray-500" onClick={() => router.push(`/admin/committee/${member.id}`)}>Edit</Button>
                                <Button variant="ghost" size="sm" className="text-red-500 hover:bg-red-50" onClick={() => toast.success("Access revoked (Demo)")}>Remove</Button>
                            </div>
                        </CardContent>
                    </Card>
                ))}
            </div>
        </div>
    );
}

"use client";

import { useEffect, useState } from "react";
import { useParams, useRouter } from "next/navigation";
import {
    ArrowLeft,
    MoreHorizontal,
    ShieldAlert,
    MessageSquare,
    CheckCircle2,
    Clock,
    AlertTriangle
} from "lucide-react";
import { complaintAPI } from "@/lib/api";
import { Complaint } from "@/types";
import { Button } from "@/components/ui/Button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/Card";
import { getStatusColor, formatRelativeTime } from "@/lib/utils";
import { toast } from "sonner";

export default function AdminComplaintDetailPage() {
    const params = useParams();
    const router = useRouter();
    const [complaint, setComplaint] = useState<Complaint | null>(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        if (params.id) {
            fetchComplaint(params.id as string);
        }
    }, [params.id]);

    const fetchComplaint = async (id: string) => {
        try {
            const response = await complaintAPI.getComplaintById(id);
            if (response.data.success) {
                setComplaint(response.data.data.complaint);
            }
        } catch (error) {
            console.error("Failed to fetch complaint:", error);
            toast.error("Failed to load complaint details");
        } finally {
            setIsLoading(false);
        }
    };

    const handleStatusUpdate = async (newStatus: string) => {
        if (!complaint) return;
        try {
            await complaintAPI.updateStatus(complaint._id, newStatus, "Status updated via Admin Console");
            toast.success(`Status updated to ${newStatus}`);
            fetchComplaint(complaint._id); // Refresh
        } catch (error) {
            toast.error("Failed to update status");
        }
    };

    const handleRevealIdentity = async () => {
        // Mock implementation for now - fully implemented in backend
        toast.info("This action requires higher level authorization confirmation (Demo Mode)");
    };

    if (isLoading) return <div className="p-8 text-center text-gray-500">Loading details...</div>;
    if (!complaint) return <div className="p-8 text-center text-gray-500">Complaint not found</div>;

    return (
        <div className="max-w-4xl mx-auto space-y-6">
            <Button variant="ghost" className="mb-4" onClick={() => router.back()}>
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Complaints
            </Button>

            {/* Header Section */}
            <div className="flex justify-between items-start">
                <div>
                    <h1 className="text-2xl font-bold text-gray-900">{complaint.title}</h1>
                    <div className="flex items-center gap-3 mt-2">
                        <span className="text-sm text-gray-500">ID: {complaint.trackingId}</span>
                        <span className="text-gray-300">•</span>
                        <span className="text-sm text-gray-500 capitalize">{complaint.category.replace("-", " ")}</span>
                        <span className="text-gray-300">•</span>
                        <span className="text-sm text-gray-500">{formatRelativeTime(complaint.createdAt)}</span>
                    </div>
                </div>
                <div className="flex gap-2">
                    <Button variant="outline" onClick={() => router.push(`/chat/${complaint.trackingId}`)}>
                        <MessageSquare className="w-4 h-4 mr-2" />
                        Chat with Student
                    </Button>
                    {/* Only show Reveal Identity for Admins (controlled by API mostly, but UI hint here) */}
                    <Button variant="destructive" onClick={handleRevealIdentity}>
                        <ShieldAlert className="w-4 h-4 mr-2" />
                        Reveal Identity
                    </Button>
                </div>
            </div>

            {/* Main Content Grid */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">

                {/* Left Column: Description & Details */}
                <div className="md:col-span-2 space-y-6">
                    <Card>
                        <CardContent className="pt-6">
                            <h3 className="text-sm font-semibold text-gray-900 mb-2">Description</h3>
                            <p className="text-gray-600 whitespace-pre-wrap leading-relaxed">
                                {complaint.description}
                            </p>
                        </CardContent>
                    </Card>

                    {/* Timeline / Activity Log (Placeholder) */}
                    <Card>
                        <CardHeader>
                            <CardTitle className="text-base">Activity Log</CardTitle>
                        </CardHeader>
                        <CardContent>
                            <div className="space-y-4">
                                {complaint.statusHistory?.map((history: any, index: number) => (
                                    <div key={index} className="flex gap-3">
                                        <div className="w-2 h-2 rounded-full bg-blue-500 mt-2 flex-shrink-0" />
                                        <div>
                                            <p className="text-sm font-medium text-gray-900">
                                                Status changed to <span className="capitalize">{history.status}</span>
                                            </p>
                                            <p className="text-xs text-gray-500">{formatRelativeTime(history.changedAt)}</p>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </CardContent>
                    </Card>
                </div>

                {/* Right Column: Actions & Meta */}
                <div className="space-y-6">
                    <Card>
                        <CardHeader>
                            <CardTitle className="text-base">Status Management</CardTitle>
                        </CardHeader>
                        <CardContent className="space-y-3">
                            <div className="p-3 bg-gray-50 rounded-lg border border-gray-100 mb-4">
                                <span className="text-xs text-gray-500 uppercase font-bold tracking-wider block mb-1">Current Status</span>
                                <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-sm font-medium border ${getStatusColor(complaint.status)}`}>
                                    {complaint.status}
                                </span>
                            </div>

                            <label className="text-sm font-medium text-gray-700 block">Update Status</label>
                            <div className="grid grid-cols-1 gap-2">
                                <Button
                                    size="sm"
                                    variant={complaint.status === "in-progress" ? "default" : "outline"}
                                    onClick={() => handleStatusUpdate("in-progress")}
                                    className="justify-start"
                                >
                                    <Clock className="w-4 h-4 mr-2 text-blue-500" />
                                    Mark In Progress
                                </Button>
                                <Button
                                    size="sm"
                                    variant={complaint.status === "resolved" ? "default" : "outline"}
                                    onClick={() => handleStatusUpdate("resolved")}
                                    className="justify-start"
                                >
                                    <CheckCircle2 className="w-4 h-4 mr-2 text-green-500" />
                                    Mark Resolved
                                </Button>
                                <Button
                                    size="sm"
                                    variant={complaint.status === "under-review" ? "default" : "outline"}
                                    onClick={() => handleStatusUpdate("under-review")}
                                    className="justify-start"
                                >
                                    <AlertTriangle className="w-4 h-4 mr-2 text-amber-500" />
                                    Under Review
                                </Button>
                            </div>
                        </CardContent>
                    </Card>
                </div>
            </div>
        </div>
    );
}

"use client";

import { useEffect, useState } from "react";
import { useRouter } from "next/navigation";
import { Filter, Search, SlidersHorizontal, ArrowUpDown } from "lucide-react";
import { complaintAPI } from "@/lib/api";
import { Complaint } from "@/types";
import { Button } from "@/components/ui/Button";
import { getStatusColor, formatRelativeTime } from "@/lib/utils";

export default function AdminComplaintsPage() {
    const router = useRouter();
    const [complaints, setComplaints] = useState<Complaint[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [filter, setFilter] = useState("all");

    useEffect(() => {
        fetchComplaints();
    }, []);

    const fetchComplaints = async () => {
        try {
            const response = await complaintAPI.getAllComplaints();
            if (response.data.success) {
                setComplaints(response.data.data.complaints);
            }
        } catch (error) {
            console.error("Failed to fetch complaints:", error);
        } finally {
            setIsLoading(false);
        }
    };

    const filteredComplaints = complaints.filter(c =>
        filter === "all" ? true : c.status === filter
    );

    return (
        <div className="space-y-6">
            <div className="flex justify-between items-center">
                <h1 className="text-2xl font-bold text-gray-900">Grievances</h1>
                <div className="flex gap-2">
                    <Button variant="outline" size="sm">
                        <ArrowUpDown className="w-4 h-4 mr-2" />
                        Sort
                    </Button>
                    <Button variant="outline" size="sm">
                        <Filter className="w-4 h-4 mr-2" />
                        Filter
                    </Button>
                </div>
            </div>

            {/* Filter Tabs */}
            <div className="border-b border-gray-200">
                <nav className="-mb-px flex space-x-8">
                    {["all", "pending", "in-progress", "resolved"].map((status) => (
                        <button
                            key={status}
                            onClick={() => setFilter(status)}
                            className={`
                                whitespace-nowrap pb-4 px-1 border-b-2 font-medium text-sm
                                ${filter === status
                                    ? "border-blue-500 text-blue-600"
                                    : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"}
                            `}
                        >
                            {status.charAt(0).toUpperCase() + status.slice(1).replace("-", " ")}
                        </button>
                    ))}
                </nav>
            </div>

            {/* Complaints List */}
            <div className="bg-white shadow rounded-lg overflow-hidden">
                {isLoading ? (
                    <div className="p-8 text-center text-gray-500">Loading...</div>
                ) : filteredComplaints.length === 0 ? (
                    <div className="p-12 text-center text-gray-500">
                        No {filter !== "all" ? filter : ""} complaints found.
                    </div>
                ) : (
                    <ul className="divide-y divide-gray-200">
                        {filteredComplaints.map((complaint) => (
                            <li
                                key={complaint._id}
                                className="hover:bg-gray-50 cursor-pointer transition-colors"
                                onClick={() => router.push(`/admin/complaints/${complaint._id}`)}
                            >
                                <div className="px-6 py-4">
                                    <div className="flex items-center justify-between">
                                        <div className="flex items-center gap-3">
                                            <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium border ${getStatusColor(complaint.status)}`}>
                                                {complaint.status}
                                            </span>
                                            <span className="text-sm text-gray-500">#{complaint.trackingId}</span>
                                        </div>
                                        <div className="text-xs text-gray-500 flex items-center">
                                            <ClockIcon className="w-3 h-3 mr-1" />
                                            {formatRelativeTime(complaint.createdAt)}
                                        </div>
                                    </div>
                                    <div className="mt-2">
                                        <h3 className="text-sm font-medium text-gray-900">{complaint.title}</h3>
                                        <p className="text-sm text-gray-500 mt-1 line-clamp-1">{complaint.description}</p>
                                    </div>
                                    <div className="mt-2 text-xs text-gray-400 capitalize">
                                        {complaint.category.replace("-", " ")}
                                    </div>
                                </div>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
}

function ClockIcon(props: any) {
    return (
        <svg
            {...props}
            xmlns="http://www.w3.org/2000/svg"
            width="24"
            height="24"
            viewBox="0 0 24 24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
        >
            <circle cx="12" cy="12" r="10" />
            <polyline points="12 6 12 12 16 14" />
        </svg>
    )
}

"use client";

import { useEffect, useState } from "react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import {
    FileText,
    AlertTriangle,
    CheckCircle,
    Clock,
    TrendingUp,
    Building,
    AlertCircle
} from "lucide-react";
import { toast } from "sonner";

import { complaintAPI } from "@/lib/api";
import { Complaint } from "@/types";
import { Button } from "@/components/ui/Button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/Card";
import { getStatusColor } from "@/lib/utils";
import { getUser } from "@/lib/auth";

export default function AdminDashboardPage() {
    const router = useRouter();
    const [complaints, setComplaints] = useState<Complaint[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [user, setUser] = useState<any>(null);

    useEffect(() => {
        const currentUser = getUser();
        setUser(currentUser);
        fetchAllComplaints();
    }, []);

    const fetchAllComplaints = async () => {
        try {
            // Admin endpoint to fetch ALL complaints
            const response = await complaintAPI.getAllComplaints();
            if (response.data.success) {
                setComplaints(response.data.data.complaints);
            }
        } catch (error) {
            console.error("Failed to fetch admin complaints:", error);
        } finally {
            setIsLoading(false);
        }
    };

    const stats = {
        total: complaints.length,
        pending: complaints.filter((c) => c.status === "pending").length,
        resolved: complaints.filter((c) => c.status === "resolved").length,
        urgent: complaints.filter((c) => c.priority === "urgent").length,
    };

    return (
        <div className="space-y-8">
            <div className="flex justify-between items-center">
                <div>
                    <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
                    <p className="text-gray-600">Overview of {user?.college} grievances</p>
                </div>


                <button
                    onClick={() => toast.info("Report download feature coming soon!")}
                    className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                >
                    Download Report
                </button>
            </div>

            {/* Quick Stats */}
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                <StatCard title="Total Complaints" value={stats.total.toString()} icon={FileText} color="blue" />
                <StatCard title="Pending" value={stats.pending.toString()} icon={Clock} color="amber" />
                <StatCard title="Urgent" value={stats.urgent.toString()} icon={AlertTriangle} color="red" />
                <StatCard title="Resolved" value={stats.resolved.toString()} icon={CheckCircle} color="green" />
            </div>

            {/* Recent Complaints Table */}
            <Card>
                <CardHeader>
                    <div className="flex items-center justify-between">
                        <CardTitle>Recent Grievances</CardTitle>
                        <Button variant="outline" size="sm" onClick={() => router.push('/admin/complaints')}>View All</Button>
                    </div>
                </CardHeader>
                <CardContent>
                    {isLoading ? (
                        <div className="flex justify-center py-8">
                            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
                        </div>
                    ) : complaints.length === 0 ? (
                        <div className="text-center py-12">
                            <p className="text-gray-500">No complaints found for your college.</p>
                        </div>
                    ) : (
                        <div className="overflow-x-auto">
                            <table className="w-full text-sm text-left">
                                <thead className="text-xs text-gray-700 uppercase bg-gray-50">
                                    <tr>
                                        <th className="px-4 py-3">Tracking ID</th>
                                        <th className="px-4 py-3">Title</th>
                                        <th className="px-4 py-3">Category</th>
                                        <th className="px-4 py-3">Status</th>
                                        <th className="px-4 py-3">Priority</th>
                                        <th className="px-4 py-3">Date</th>
                                        <th className="px-4 py-3">Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {complaints.slice(0, 10).map((complaint) => (
                                        <tr key={complaint._id} className="bg-white border-b hover:bg-gray-50">
                                            <td className="px-4 py-3 font-medium text-gray-900">{complaint.trackingId}</td>
                                            <td className="px-4 py-3 truncate max-w-[200px]">{complaint.title}</td>
                                            <td className="px-4 py-3 capitalize">{complaint.category.replace("-", " ")}</td>
                                            <td className="px-4 py-3">
                                                <span className={`inline-flex items-center px-2 py-0.5 rounded text-xs font-medium border ${getStatusColor(complaint.status)}`}>
                                                    {complaint.status}
                                                </span>
                                            </td>
                                            <td className="px-4 py-3 capitalize">
                                                <span className={complaint.priority === 'urgent' ? 'text-red-600 font-bold' : ''}>
                                                    {complaint.priority}
                                                </span>
                                            </td>
                                            <td className="px-4 py-3">{new Date(complaint.createdAt).toLocaleDateString()}</td>
                                            <td className="px-4 py-3">
                                                <Link href={`/admin/complaints/${complaint._id}`} className="font-medium text-blue-600 hover:underline">
                                                    Manage
                                                </Link>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </CardContent>
            </Card>
        </div>
    );
}

function StatCard({ title, value, icon: Icon, color }: any) {
    const colors: any = {
        blue: "bg-blue-50 text-blue-600",
        amber: "bg-amber-50 text-amber-600",
        red: "bg-red-50 text-red-600",
        green: "bg-green-50 text-green-600",
    };

    return (
        <div className="bg-white p-6 rounded-xl border border-gray-200 shadow-sm flex items-center space-x-4">
            <div className={`p-3 rounded-full ${colors[color]}`}>
                <Icon className="w-6 h-6" />
            </div>
            <div>
                <p className="text-sm font-medium text-gray-500">{title}</p>
                <p className="text-2xl font-bold text-gray-900">{value}</p>
            </div>
        </div>
    )
}

