"use client";

import RoleGuard from "@/components/auth/RoleGuard";
import VerificationPending from "@/components/auth/VerificationPending";
import { LayoutDashboard, Users, FileText, Settings, LogOut, ShieldCheck } from "lucide-react";
import { logout, getUser, getOrganization } from "@/lib/auth";
import Link from "next/link";
import { useEffect, useState } from "react";

export default function AdminLayout({
    children,
}: {
    children: React.ReactNode;
}) {
    const [isVerified, setIsVerified] = useState(true);
    const [isLoading, setIsLoading] = useState(true);
    const user = getUser();
    const organization = getOrganization();

    useEffect(() => {
        // specific check: if organization data exists but isVerified is false
        if (organization && organization.isVerified === false) {
            setIsVerified(false);
        }
        setIsLoading(false);
    }, []);

    if (isLoading) return null;

    if (!isVerified) {
        return <VerificationPending orgName={organization?.name} />;
    }

    // Org Admin Navigation
    const navigation = [
        { name: "Dashboard", href: "/admin/dashboard", icon: LayoutDashboard },
        { name: "Complaints", href: "/admin/complaints", icon: FileText },
        { name: "Committee", href: "/admin/committee", icon: Users },
        { name: "Settings", href: "/admin/settings", icon: Settings },
    ];



    return (
        <RoleGuard allowedRoles={["admin"]}>
            <div className="min-h-screen bg-gray-50 flex">
                <aside className="w-64 bg-white border-r border-gray-200 fixed inset-y-0 z-30">
                    <div className="flex items-center h-16 px-6 border-b border-gray-100">
                        <ShieldCheck className="w-8 h-8 text-blue-600 mr-2" />
                        <span className="text-xl font-bold text-gray-900">VoiSafe</span>
                    </div>

                    <div className="px-6 py-4">
                        <div className="text-xs font-semibold text-gray-400 uppercase tracking-wider mb-2">
                            {user?.college || "Organization"}
                        </div>
                        <div className="text-sm font-medium text-gray-900 truncate">
                            Admin Console
                        </div>
                    </div>

                    <nav className="px-4 space-y-1 mt-2">
                        {navigation.map((item) => (
                            <Link
                                key={item.name}
                                href={item.href}
                                className="flex items-center space-x-3 px-4 py-3 text-gray-600 hover:bg-blue-50 hover:text-blue-600 rounded-lg transition-colors group"
                            >
                                <item.icon className="w-5 h-5 group-hover:text-blue-600 transition-colors" />
                                <span className="font-medium">{item.name}</span>
                            </Link>
                        ))}
                    </nav>

                    <div className="absolute bottom-0 w-full p-4 border-t border-gray-100">
                        <button
                            onClick={() => logout()}
                            className="flex items-center space-x-3 px-4 py-3 text-red-500 hover:bg-red-50 w-full rounded-lg transition-colors"
                        >
                            <LogOut className="w-5 h-5" />
                            <span className="font-medium">Logout</span>
                        </button>
                    </div>
                </aside>

                <main className="flex-1 ml-64 p-8">
                    {children}
                </main>
            </div>
        </RoleGuard>
    );
}

"use client";

import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useRouter } from "next/navigation";
import Link from "next/link";
import { toast } from "sonner";
import { LogIn } from "lucide-react";

import { loginSchema, type LoginFormData } from "@/schemas/complaint";
import { authAPI } from "@/lib/api";
import { saveAuth } from "@/lib/auth";
import { Button } from "@/components/ui/Button";
import { Input } from "@/components/ui/Input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/Card";

export default function LoginPage() {
    const router = useRouter();
    const [isLoading, setIsLoading] = useState(false);

    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm<LoginFormData>({
        resolver: zodResolver(loginSchema),
        defaultValues: {
            email: "john.doe@college.edu",
            password: "password123",
        },
    });

    const onSubmit = async (data: LoginFormData) => {
        setIsLoading(true);

        try {
            const response = await authAPI.login(data.email, data.password);

            if (response.data.success) {
                const { token, user, organization } = response.data.data;

                // Save auth data
                saveAuth(token, user, organization);

                toast.success("Login successful!");

                // Redirect to dashboard
                router.push("/dashboard");
            }
        } catch (error: any) {
            const message = error.response?.data?.message || "Login failed. Please try again.";
            toast.error(message);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 via-white to-purple-50 p-4">
            <Card className="w-full max-w-md">
                <CardHeader className="space-y-1 text-center">
                    <div className="flex justify-center mb-4">
                        <div className="p-3 bg-blue-100 rounded-full">
                            <LogIn className="w-8 h-8 text-blue-600" />
                        </div>
                    </div>
                    <CardTitle className="text-2xl font-bold">Welcome to VoiSafe</CardTitle>
                    <CardDescription>
                        Login to access your anonymous grievance dashboard
                    </CardDescription>
                </CardHeader>
                <CardContent>
                    <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
                        <Input
                            label="Email"
                            type="email"
                            placeholder="your.email@college.edu"
                            error={errors.email?.message}
                            {...register("email")}
                            required
                        />

                        <Input
                            label="Password"
                            type="password"
                            placeholder="••••••••"
                            error={errors.password?.message}
                            {...register("password")}
                            required
                        />

                        <Button
                            type="submit"
                            className="w-full"
                            isLoading={isLoading}
                        >
                            {isLoading ? "Logging in..." : "Login"}
                        </Button>

                        <p className="text-center text-sm text-gray-600">
                            Don't have an account?{" "}
                            <Link href="/register" className="text-blue-600 hover:underline font-medium">
                                Register here
                            </Link>
                        </p>
                    </form>
                </CardContent>
            </Card>
        </div>
    );
}

"use client";

import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useRouter } from "next/navigation";
import Link from "next/link";
import { toast } from "sonner";
import { UserPlus } from "lucide-react";

import { registerSchema, type RegisterFormData } from "@/schemas/complaint";
import { authAPI } from "@/lib/api";
import { saveAuth } from "@/lib/auth";
import { Button } from "@/components/ui/Button";
import { Input } from "@/components/ui/Input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/Card";

export default function RegisterPage() {
    const router = useRouter();
    const [isLoading, setIsLoading] = useState(false);

    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm<RegisterFormData>({
        resolver: zodResolver(registerSchema),
        defaultValues: {
            name: "John Doe",
            email: "john.doe@college.edu",
            password: "password123",
            confirmPassword: "password123",
            college: "ABC University",
            studentId: "STU123456",
            department: "Computer Science",
            year: 2,
        },
    });

    const onSubmit = async (data: RegisterFormData) => {
        setIsLoading(true);

        try {
            const response = await authAPI.register({
                ...data,
                role: "student", // Default role for registration
            });

            if (response.data.success) {
                const { token, user } = response.data.data;

                // Save auth data
                saveAuth(token, user);

                toast.success("Registration successful!");

                // Redirect to dashboard
                router.push("/dashboard");
            }
        } catch (error: any) {
            const message = error.response?.data?.message || "Registration failed. Please try again.";
            toast.error(message);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 via-white to-purple-50 p-4">
            <Card className="w-full max-w-2xl">
                <CardHeader className="space-y-1 text-center">
                    <div className="flex justify-center mb-4">
                        <div className="p-3 bg-blue-100 rounded-full">
                            <UserPlus className="w-8 h-8 text-blue-600" />
                        </div>
                    </div>
                    <CardTitle className="text-2xl font-bold">Create Your Account</CardTitle>
                    <CardDescription>
                        Register to submit anonymous complaints and track their status
                    </CardDescription>
                </CardHeader>
                <CardContent>
                    <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <Input
                                label="Full Name"
                                placeholder="John Doe"
                                error={errors.name?.message}
                                {...register("name")}
                                required
                            />

                            <Input
                                label="Email"
                                type="email"
                                placeholder="your.email@college.edu"
                                error={errors.email?.message}
                                {...register("email")}
                                required
                            />
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <Input
                                label="Password"
                                type="password"
                                placeholder="••••••••"
                                error={errors.password?.message}
                                {...register("password")}
                                required
                            />

                            <Input
                                label="Confirm Password"
                                type="password"
                                placeholder="••••••••"
                                error={errors.confirmPassword?.message}
                                {...register("confirmPassword")}
                                required
                            />
                        </div>

                        <Input
                            label="College/Institution"
                            placeholder="ABC University"
                            error={errors.college?.message}
                            {...register("college")}
                            required
                        />

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <Input
                                label="Student ID"
                                placeholder="STU123456"
                                error={errors.studentId?.message}
                                {...register("studentId")}
                                required
                            />

                            <Input
                                label="Department"
                                placeholder="Computer Science"
                                error={errors.department?.message}
                                {...register("department")}
                                required
                            />
                        </div>

                        <Input
                            label="Year of Study"
                            type="number"
                            placeholder="1"
                            min={1}
                            max={5}
                            error={errors.year?.message}
                            {...register("year", { valueAsNumber: true })}
                            required
                        />

                        <Button
                            type="submit"
                            className="w-full"
                            isLoading={isLoading}
                        >
                            {isLoading ? "Creating account..." : "Create Account"}
                        </Button>

                        <p className="text-center text-sm text-gray-600">
                            Already have an account?{" "}
                            <Link href="/login" className="text-blue-600 hover:underline font-medium">
                                Login here
                            </Link>
                        </p>
                    </form>
                </CardContent>
            </Card>
        </div>
    );
}

"use client";

import { useEffect, useState } from "react";
import { useParams, useRouter } from "next/navigation";
import {
    ArrowLeft,
    MessageSquare,
    CheckCircle2,
    Clock,
    AlertTriangle,
    FileText
} from "lucide-react";
import { complaintAPI } from "@/lib/api";
import { Complaint } from "@/types";
import { Button } from "@/components/ui/Button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/Card";
import { getStatusColor, formatRelativeTime } from "@/lib/utils";
import { toast } from "sonner";

export default function CommitteeComplaintDetailPage() {
    const params = useParams();
    const router = useRouter();
    const [complaint, setComplaint] = useState<Complaint | null>(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        if (params.id) {
            fetchComplaint(params.id as string);
        }
    }, [params.id]);

    const fetchComplaint = async (id: string) => {
        try {
            const response = await complaintAPI.getComplaintById(id);
            if (response.data.success) {
                setComplaint(response.data.data.complaint);
            }
        } catch (error) {
            console.error("Failed to fetch complaint:", error);
            toast.error("Failed to load complaint details");
        } finally {
            setIsLoading(false);
        }
    };

    const handleStatusUpdate = async (newStatus: string) => {
        if (!complaint) return;
        try {
            await complaintAPI.updateStatus(complaint._id, newStatus, "Status updated by Committee Member");
            toast.success(`Status updated to ${newStatus}`);
            fetchComplaint(complaint._id); // Refresh
        } catch (error) {
            toast.error("Failed to update status");
        }
    };

    if (isLoading) return <div className="p-8 text-center text-slate-500">Loading details...</div>;
    if (!complaint) return <div className="p-8 text-center text-slate-500">Complaint not found</div>;

    return (
        <div className="max-w-4xl mx-auto space-y-6">
            <Button variant="ghost" className="mb-4 text-slate-600" onClick={() => router.back()}>
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Workspace
            </Button>

            {/* Header Section */}
            <div className="flex justify-between items-start">
                <div>
                    <h1 className="text-2xl font-bold text-slate-900">{complaint.title}</h1>
                    <div className="flex items-center gap-3 mt-2">
                        <span className="text-sm text-slate-500">#{complaint.trackingId}</span>
                        <span className="text-slate-300">•</span>
                        <span className="text-sm text-slate-600 font-medium uppercase tracking-wide">{complaint.category.replace("-", " ")}</span>
                        <span className="text-slate-300">•</span>
                        <span className="text-sm text-slate-500">{formatRelativeTime(complaint.createdAt)}</span>
                    </div>
                </div>
                <Button variant="outline" onClick={() => router.push(`/chat/${complaint.trackingId}`)}>
                    <MessageSquare className="w-4 h-4 mr-2" />
                    Open Chat
                </Button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {/* Left Column */}
                <div className="md:col-span-2 space-y-6">
                    <Card>
                        <CardHeader>
                            <CardTitle className="flex items-center text-lg text-slate-800">
                                <FileText className="w-5 h-5 mr-2 text-indigo-600" />
                                Complaint Details
                            </CardTitle>
                        </CardHeader>
                        <CardContent>
                            <p className="text-slate-600 whitespace-pre-wrap leading-relaxed">
                                {complaint.description}
                            </p>
                        </CardContent>
                    </Card>

                    {/* Timeline */}
                    <Card>
                        <CardHeader><CardTitle className="text-base">History</CardTitle></CardHeader>
                        <CardContent>
                            <div className="space-y-4">
                                {complaint.statusHistory?.map((history: any, index: number) => (
                                    <div key={index} className="flex gap-3">
                                        <div className="w-2 h-2 rounded-full bg-slate-200 mt-2 ring-4 ring-white" />
                                        <div>
                                            <p className="text-sm font-medium text-slate-900">
                                                Status: <span className="capitalize">{history.status}</span>
                                            </p>
                                            <p className="text-xs text-slate-500">{formatRelativeTime(history.changedAt)}</p>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </CardContent>
                    </Card>
                </div>

                {/* Right Column: Actions */}
                <div className="space-y-6">
                    <Card className="border-indigo-100 bg-indigo-50/50">
                        <CardHeader>
                            <CardTitle className="text-base text-indigo-900">Action Required</CardTitle>
                        </CardHeader>
                        <CardContent className="space-y-3">
                            <div className="mb-4">
                                <span className="text-xs text-slate-500 font-semibold uppercase">Current Status</span>
                                <div className={`inline-flex items-center px-3 py-1 mt-1 rounded-full text-sm font-medium border ${getStatusColor(complaint.status)}`}>
                                    {complaint.status}
                                </div>
                            </div>

                            <Button
                                size="sm"
                                className="w-full justify-start bg-white hover:bg-slate-50 text-slate-700 border border-slate-200"
                                onClick={() => handleStatusUpdate("in-progress")}
                            >
                                <Clock className="w-4 h-4 mr-2 text-indigo-500" />
                                Mark as In Progress
                            </Button>

                            <Button
                                size="sm"
                                className="w-full justify-start bg-white hover:bg-slate-50 text-slate-700 border border-slate-200"
                                onClick={() => handleStatusUpdate("resolved")}
                            >
                                <CheckCircle2 className="w-4 h-4 mr-2 text-green-500" />
                                Mark as Resolved
                            </Button>

                            <Button
                                size="sm"
                                className="w-full justify-start bg-white hover:bg-slate-50 text-slate-700 border border-slate-200"
                                onClick={() => handleStatusUpdate("under-review")}
                            >
                                <AlertTriangle className="w-4 h-4 mr-2 text-amber-500" />
                                Needs Review
                            </Button>
                        </CardContent>
                    </Card>
                </div>
            </div>
        </div>
    );
}

"use client";

import { useEffect, useState } from "react";
import { useRouter } from "next/navigation";
import { CheckSquare, AlertCircle, Clock, CheckCircle2, MoreHorizontal } from "lucide-react";
import { complaintAPI } from "@/lib/api";
import { Complaint } from "@/types";
import { formatRelativeTime, getStatusColor } from "@/lib/utils";
import { Button } from "@/components/ui/Button";

export default function CommitteeWorkspacePage() {
    const router = useRouter();
    const [complaints, setComplaints] = useState<Complaint[]>([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        fetchComplaints();
    }, []);

    const fetchComplaints = async () => {
        try {
            const response = await complaintAPI.getAllComplaints();
            if (response.data.success) {
                setComplaints(response.data.data.complaints);
            }
        } catch (error) {
            console.error("Failed to fetch complaints:", error);
        } finally {
            setIsLoading(false);
        }
    };

    const activeComplaints = complaints.filter(c => !["resolved", "closed"].includes(c.status));

    return (
        <div className="space-y-6">
            <h1 className="text-2xl font-bold text-slate-800">My Workspace</h1>

            <div className="bg-white rounded-lg border border-slate-200 shadow-sm overflow-hidden">
                <div className="bg-slate-50 px-6 py-4 border-b border-slate-200 flex justify-between items-center">
                    <h2 className="font-semibold text-slate-700 flex items-center">
                        <CheckSquare className="w-5 h-5 mr-2 text-indigo-600" />
                        Active Grievances
                    </h2>
                    <span className="bg-indigo-100 text-indigo-700 text-xs px-2.5 py-0.5 rounded-full font-medium">
                        {activeComplaints.length} Active
                    </span>
                </div>

                <div className="divide-y divide-slate-100">
                    {isLoading ? (
                        <div className="p-8 text-center text-slate-500">Loading complaints...</div>
                    ) : complaints.length === 0 ? (
                        <div className="p-12 text-center text-slate-500">
                            <CheckCircle2 className="w-12 h-12 mx-auto mb-3 text-slate-300" />
                            <p>No complaints found.</p>
                        </div>
                    ) : (
                        complaints.map((complaint) => (
                            <div
                                key={complaint._id}
                                className="p-6 hover:bg-slate-50 transition-colors cursor-pointer group"
                                onClick={() => router.push(`/committee/complaints/${complaint._id}`)}
                            >
                                <div className="flex justify-between items-start">
                                    <div className="flex-1 min-w-0 pr-4">
                                        <div className="flex items-center gap-2 mb-1">
                                            <span className={`inline-flex items-center px-2 py-0.5 rounded text-xs font-medium border ${getStatusColor(complaint.status)}`}>
                                                {complaint.status}
                                            </span>
                                            <span className="text-xs text-slate-400">•</span>
                                            <span className="text-xs text-slate-500 font-medium uppercase tracking-wide">
                                                {complaint.category}
                                            </span>
                                        </div>
                                        <h3 className="font-medium text-slate-900 group-hover:text-indigo-600 truncate">
                                            {complaint.title}
                                        </h3>
                                        <p className="text-sm text-slate-500 mt-1 line-clamp-2">
                                            {complaint.description}
                                        </p>
                                        <div className="flex items-center mt-3 text-xs text-slate-400">
                                            <Clock className="w-3.5 h-3.5 mr-1" />
                                            {formatRelativeTime(complaint.createdAt)}
                                        </div>
                                    </div>

                                    <Button variant="ghost" size="sm" className="opacity-0 group-hover:opacity-100 transition-opacity">
                                        View Details
                                    </Button>
                                </div>
                            </div>
                        ))
                    )}
                </div>
            </div>
        </div>
    );
}

"use client";

import { useEffect, useState, useRef } from "react";
import { useParams } from "next/navigation";
import { toast } from "sonner";
import { Send, Loader2 } from "lucide-react";

import { ChatMessage } from "@/types";
import {
    initializeSocket,
    joinChatRoom,
    sendMessage,
    sendTyping,
    socketEvents,
    disconnectSocket
} from "@/lib/socket";
import { Button } from "@/components/ui/Button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/Card";
import { formatDateTime } from "@/lib/utils";

export default function ChatPage() {
    const params = useParams();
    const trackingId = params.trackingId as string;

    const [messages, setMessages] = useState<ChatMessage[]>([]);
    const [newMessage, setNewMessage] = useState("");
    const [isTyping, setIsTyping] = useState(false);
    const [isConnected, setIsConnected] = useState(false);
    const [isSending, setIsSending] = useState(false);

    const messagesEndRef = useRef<HTMLDivElement>(null);
    const typingTimeoutRef = useRef<NodeJS.Timeout | undefined>(undefined);

    useEffect(() => {
        // Initialize socket connection
        try {
            const socket = initializeSocket();
            setIsConnected(true);

            // Join chat room
            joinChatRoom(trackingId);

            // Set up event listeners
            socketEvents.onChatHistory((data) => {
                setMessages(data.messages);
            });

            socketEvents.onMessageReceived((data) => {
                setMessages((prev) => [...prev, data]);
            });

            socketEvents.onUserTyping((data) => {
                if (data.role !== "student") {
                    setIsTyping(data.isTyping);
                }
            });

            socketEvents.onError((data) => {
                toast.error(data.message);
            });

            return () => {
                disconnectSocket();
            };
        } catch (error: any) {
            toast.error(error.message || "Failed to connect to chat");
        }
    }, [trackingId]);

    useEffect(() => {
        // Scroll to bottom when new messages arrive
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    const handleSendMessage = () => {
        if (!newMessage.trim() || isSending) return;

        setIsSending(true);
        try {
            sendMessage(trackingId, newMessage.trim());
            setNewMessage("");
        } catch (error: any) {
            toast.error(error.message || "Failed to send message");
        } finally {
            setIsSending(false);
        }
    };

    const handleTyping = (value: string) => {
        setNewMessage(value);

        // Clear existing timeout
        if (typingTimeoutRef.current) {
            clearTimeout(typingTimeoutRef.current);
        }

        // Send typing indicator
        sendTyping(trackingId, true);

        // Stop typing after 2 seconds of inactivity
        typingTimeoutRef.current = setTimeout(() => {
            sendTyping(trackingId, false);
        }, 2000);
    };

    const handleKeyPress = (e: React.KeyboardEvent) => {
        if (e.key === "Enter" && !e.shiftKey) {
            e.preventDefault();
            handleSendMessage();
        }
    };

    return (
        <div className="max-w-4xl mx-auto">
            <div className="mb-6">
                <h1 className="text-3xl font-bold text-gray-900 mb-2">Anonymous Chat</h1>
                <p className="text-gray-600">
                    Chat with administrators about your complaint. Your identity remains anonymous.
                </p>
            </div>

            <Card className="h-[600px] flex flex-col">
                <CardHeader className="border-b">
                    <div className="flex items-center justify-between">
                        <CardTitle>Messages</CardTitle>
                        <div className="flex items-center gap-2">
                            <div className={`w-2 h-2 rounded-full ${isConnected ? "bg-green-500" : "bg-red-500"}`}></div>
                            <span className="text-sm text-gray-600">
                                {isConnected ? "Connected" : "Disconnected"}
                            </span>
                        </div>
                    </div>
                </CardHeader>

                <CardContent className="flex-1 overflow-y-auto p-6 space-y-4">
                    {messages.length === 0 ? (
                        <div className="flex items-center justify-center h-full text-gray-500">
                            <p>No messages yet. Start the conversation!</p>
                        </div>
                    ) : (
                        messages.map((message) => {
                            const isStudent = message.senderRole === "student";

                            return (
                                <div
                                    key={message._id}
                                    className={`flex ${isStudent ? "justify-end" : "justify-start"}`}
                                >
                                    <div
                                        className={`max-w-[70%] rounded-lg px-4 py-2 ${isStudent
                                            ? "bg-blue-600 text-white"
                                            : "bg-gray-100 text-gray-900"
                                            }`}
                                    >
                                        {!isStudent && message.senderName && (
                                            <p className="text-xs font-medium mb-1 opacity-75">
                                                {message.senderName} (Admin)
                                            </p>
                                        )}
                                        <p className="text-sm whitespace-pre-wrap">{message.message}</p>
                                        <p className={`text-xs mt-1 ${isStudent ? "text-blue-100" : "text-gray-500"}`}>
                                            {formatDateTime(message.createdAt)}
                                        </p>
                                    </div>
                                </div>
                            );
                        })
                    )}

                    {isTyping && (
                        <div className="flex justify-start">
                            <div className="bg-gray-100 rounded-lg px-4 py-2">
                                <div className="flex gap-1">
                                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: "0ms" }}></div>
                                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: "150ms" }}></div>
                                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: "300ms" }}></div>
                                </div>
                            </div>
                        </div>
                    )}

                    <div ref={messagesEndRef} />
                </CardContent>

                <div className="border-t p-4">
                    <div className="flex gap-2">
                        <textarea
                            value={newMessage}
                            onChange={(e) => handleTyping(e.target.value)}
                            onKeyPress={handleKeyPress}
                            placeholder="Type your message... (Press Enter to send)"
                            className="flex-1 resize-none rounded-lg border border-gray-300 px-4 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                            rows={2}
                            disabled={!isConnected}
                        />
                        <Button
                            onClick={handleSendMessage}
                            disabled={!newMessage.trim() || isSending || !isConnected}
                            className="self-end"
                        >
                            {isSending ? (
                                <Loader2 className="w-4 h-4 animate-spin" />
                            ) : (
                                <Send className="w-4 h-4" />
                            )}
                        </Button>
                    </div>
                </div>
            </Card>
        </div>
    );
}

"use client";

import { useEffect, useState } from "react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import {
    FileText,
    MessageSquare,
    Clock,
    CheckCircle2,
    AlertCircle,
    TrendingUp,
    Users
} from "lucide-react";

import { Complaint } from "@/types";
import { complaintAPI } from "@/lib/api";
import { getUser, getOrganization } from "@/lib/auth";
import { Button } from "@/components/ui/Button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/Card";
import { formatRelativeTime, getStatusColor } from "@/lib/utils";

// ----------------------------------------------------------------------
// STUDENT DASHBOARD COMPONENT
// ----------------------------------------------------------------------
export default function DashboardPage() {
    const router = useRouter();
    const [user, setUser] = useState<any>(null);
    const [complaints, setComplaints] = useState<Complaint[]>([]);
    const [isLoading, setIsLoading] = useState(true);

    const [organization, setOrganization] = useState<any>(null);

    useEffect(() => {
        const currentUser = getUser();
        if (!currentUser) {
            router.push("/login"); // RoleGuard doubles this check but safe to keep
            return;
        }
        setUser(currentUser);
        setOrganization(getOrganization());
        fetchComplaints();
    }, [router]);

    const fetchComplaints = async () => {
        try {
            const response = await complaintAPI.getMyComplaints();
            if (response.data.success) {
                setComplaints(response.data.data.complaints);
            }
        } catch (error) {
            console.error("Failed to fetch complaints:", error);
        } finally {
            setIsLoading(false);
        }
    };

    const stats = {
        total: complaints.length,
        pending: complaints.filter((c) => c.status === "pending").length,
        resolved: complaints.filter((c) => c.status === "resolved").length,
        inProgress: complaints.filter((c) => ["in-progress", "under-review"].includes(c.status)).length,
    };

    if (!user) return null;

    return (
        <div className="space-y-8">
            {organization && organization.isVerified === false && (
                <div className="bg-amber-50 border border-amber-200 rounded-lg p-4 flex items-start gap-3">
                    <AlertCircle className="w-5 h-5 text-amber-600 mt-0.5 flex-shrink-0" />
                    <div>
                        <h3 className="text-sm font-semibold text-amber-900">System Activation Pending</h3>
                        <p className="text-sm text-amber-700 mt-1">
                            Your institution ({organization.name}) is currently pending verification. You cannot submit new complaints until the approval process is complete.
                        </p>
                    </div>
                </div>
            )}

            <div>
                <h1 className="text-3xl font-bold text-gray-900 mb-2">Welcome back, {user?.name}!</h1>
                <p className="text-gray-600">Here's an overview of your complaints and recent activity.</p>
            </div>

            {/* Stats Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <Card>
                    <CardContent className="pt-6">
                        <div className="flex items-center justify-between">
                            <div>
                                <p className="text-sm font-medium text-gray-600">Total Complaints</p>
                                <p className="text-3xl font-bold text-gray-900 mt-2">{stats.total}</p>
                            </div>
                            <div className="p-3 bg-blue-100 rounded-lg">
                                <FileText className="w-6 h-6 text-blue-600" />
                            </div>
                        </div>
                    </CardContent>
                </Card>
                <Card>
                    <CardContent className="pt-6">
                        <div className="flex items-center justify-between">
                            <div>
                                <p className="text-sm font-medium text-gray-600">Pending</p>
                                <p className="text-3xl font-bold text-yellow-600 mt-2">{stats.pending}</p>
                            </div>
                            <div className="p-3 bg-yellow-100 rounded-lg">
                                <Clock className="w-6 h-6 text-yellow-600" />
                            </div>
                        </div>
                    </CardContent>
                </Card>
                <Card>
                    <CardContent className="pt-6">
                        <div className="flex items-center justify-between">
                            <div>
                                <p className="text-sm font-medium text-gray-600">In Progress</p>
                                <p className="text-3xl font-bold text-purple-600 mt-2">{stats.inProgress}</p>
                            </div>
                            <div className="p-3 bg-purple-100 rounded-lg">
                                <TrendingUp className="w-6 h-6 text-purple-600" />
                            </div>
                        </div>
                    </CardContent>
                </Card>
                <Card>
                    <CardContent className="pt-6">
                        <div className="flex items-center justify-between">
                            <div>
                                <p className="text-sm font-medium text-gray-600">Resolved</p>
                                <p className="text-3xl font-bold text-green-600 mt-2">{stats.resolved}</p>
                            </div>
                            <div className="p-3 bg-green-100 rounded-lg">
                                <CheckCircle2 className="w-6 h-6 text-green-600" />
                            </div>
                        </div>
                    </CardContent>
                </Card>
            </div>

            {/* Quick Actions */}
            <Card>
                <CardHeader>
                    <CardTitle>Quick Actions</CardTitle>
                </CardHeader>
                <CardContent>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <Button
                            onClick={() => router.push("/submit-complaint")}
                            className="h-auto py-4 justify-start"
                            disabled={organization?.isVerified === false}
                        >
                            <FileText className="w-5 h-5 mr-3" />
                            <div className="text-left">
                                <p className="font-semibold">Submit New Complaint</p>
                                <p className="text-sm opacity-90">File a new anonymous grievance</p>
                            </div>
                        </Button>
                        <Button variant="outline" onClick={() => router.push("/my-complaints")} className="h-auto py-4 justify-start">
                            <MessageSquare className="w-5 h-5 mr-3" />
                            <div className="text-left">
                                <p className="font-semibold">View My Complaints</p>
                                <p className="text-sm opacity-75">Track and manage your complaints</p>
                            </div>
                        </Button>
                    </div>
                </CardContent>
            </Card>

            {/* Recent Complaints */}
            <Card>
                <CardHeader>
                    <div className="flex items-center justify-between">
                        <CardTitle>Recent Complaints</CardTitle>
                        <Link href="/my-complaints" className="text-sm text-blue-600 hover:underline">View all</Link>
                    </div>
                </CardHeader>
                <CardContent>
                    {isLoading ? (
                        <div className="flex justify-center py-8">
                            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
                        </div>
                    ) : complaints.length === 0 ? (
                        <div className="text-center py-12">
                            <AlertCircle className="w-12 h-12 text-gray-400 mx-auto mb-3" />
                            <p className="text-gray-600 mb-4">You haven't submitted any complaints yet.</p>
                            <Button onClick={() => router.push("/submit-complaint")}>Submit Your First Complaint</Button>
                        </div>
                    ) : (
                        <div className="space-y-4">
                            {complaints.slice(0, 5).map((complaint) => (
                                <div
                                    key={complaint._id}
                                    className="flex items-center justify-between p-4 border border-gray-200 rounded-lg hover:border-blue-300 transition-colors cursor-pointer"
                                    onClick={() => router.push(`/track/${complaint.trackingId}`)}
                                >
                                    <div className="flex-1 min-w-0">
                                        <h4 className="font-medium text-gray-900 truncate">{complaint.title}</h4>
                                        <div className="flex items-center gap-3 mt-1">
                                            <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium border ${getStatusColor(complaint.status)}`}>
                                                {complaint.status.replace("-", " ")}
                                            </span>
                                            <span className="text-sm text-gray-500">{formatRelativeTime(complaint.createdAt)}</span>
                                        </div>
                                    </div>
                                    <Button variant="ghost" size="sm" onClick={(e) => { e.stopPropagation(); router.push(`/chat/${complaint.trackingId}`); }}>
                                        <MessageSquare className="w-4 h-4" />
                                    </Button>
                                </div>
                            ))}
                        </div>
                    )}
                </CardContent>
            </Card>
        </div>
    );
}

