"use client";

import { useEffect, useState } from "react";
import { useRouter, usePathname } from "next/navigation";
import RoleGuard from "@/components/auth/RoleGuard";
import Link from "next/link";
import {
    LayoutDashboard,
    FileText,
    MessageSquare,
    List,
    LogOut,
    Menu,
    X
} from "lucide-react";

import { getUser, logout } from "@/lib/auth";
import { User } from "@/types";
import { Button } from "@/components/ui/Button";

export default function DashboardLayout({
    children,
}: {
    children: React.ReactNode;
}) {
    const router = useRouter();
    const pathname = usePathname();
    const [user, setUser] = useState<User | null>(null);
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    useEffect(() => {
        const currentUser = getUser();
        setUser(currentUser);
    }, [router]);

    const navigation = [
        { name: "Dashboard", href: "/dashboard", icon: LayoutDashboard },
        { name: "Submit Complaint", href: "/submit-complaint", icon: FileText },
        { name: "My Complaints", href: "/my-complaints", icon: List },
    ];

    const handleLogout = () => {
        logout();
    };

    return (
        <RoleGuard allowedRoles={['student']}>
            <div className="min-h-screen bg-gray-50">
                {/* ... rest of the layout ... */}

                {/* Mobile sidebar backdrop */}
                {isSidebarOpen && (
                    <div
                        className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden"
                        onClick={() => setIsSidebarOpen(false)}
                    />
                )}

                {/* Sidebar */}
                <aside
                    className={`fixed inset-y-0 left-0 z-50 w-64 bg-white border-r border-gray-200 transform transition-transform duration-200 ease-in-out lg:translate-x-0 ${isSidebarOpen ? "translate-x-0" : "-translate-x-full"
                        }`}
                >
                    <div className="flex flex-col h-full">
                        {/* Logo */}
                        <div className="flex items-center justify-between h-16 px-6 border-b border-gray-200">
                            <h1 className="text-xl font-bold text-blue-600">VoiSafe</h1>
                            <button
                                onClick={() => setIsSidebarOpen(false)}
                                className="lg:hidden"
                            >
                                <X className="w-6 h-6 text-gray-900" />
                            </button>
                        </div>

                        {/* Navigation */}
                        <nav className="flex-1 px-4 py-6 space-y-2">
                            {navigation.map((item) => {
                                const isActive = pathname === item.href;
                                return (
                                    <Link
                                        key={item.name}
                                        href={item.href}
                                        className={`flex items-center gap-3 px-4 py-3 rounded-lg transition-colors ${isActive
                                            ? "bg-blue-50 text-blue-600 font-medium"
                                            : "text-gray-700 hover:bg-gray-50"
                                            }`}
                                        onClick={() => setIsSidebarOpen(false)}
                                    >
                                        <item.icon className="w-5 h-5" />
                                        {item.name}
                                    </Link>
                                );
                            })}
                        </nav>

                        {/* User section */}
                        <div className="p-4 border-t border-gray-200">
                            {user && (
                                <div className="flex items-center gap-3 px-4 py-3 bg-gray-50 rounded-lg mb-2">
                                    <div className="w-10 h-10 rounded-full bg-blue-100 flex items-center justify-center">
                                        <span className="text-blue-600 font-semibold">
                                            {user.name?.charAt(0).toUpperCase()}
                                        </span>
                                    </div>
                                    <div className="flex-1 min-w-0">
                                        <p className="text-sm font-medium text-gray-900 truncate">
                                            {user.name}
                                        </p>
                                        <p className="text-xs text-gray-500 truncate">{user.email}</p>
                                    </div>
                                </div>
                            )}
                            <Button
                                variant="outline"
                                className="w-full"
                                onClick={handleLogout}
                            >
                                <LogOut className="w-4 h-4" />
                                Logout
                            </Button>
                        </div>
                    </div>
                </aside>

                {/* Main content */}
                <div className="lg:pl-64">
                    {/* Mobile header */}
                    <header className="sticky top-0 z-30 flex items-center h-16 px-4 bg-white border-b border-gray-200 lg:hidden">
                        <button
                            onClick={() => setIsSidebarOpen(true)}
                            className="p-2 -ml-2 rounded-lg hover:bg-gray-100"
                        >
                            <Menu className="w-6 h-6 text-gray-900" />
                        </button>
                        <h1 className="ml-4 text-lg font-semibold text-gray-900">VoiSafe</h1>
                    </header>

                    {/* Page content */}
                    <main className="p-6 lg:p-8">{children}</main>
                </div>
            </div>
        </RoleGuard>
    );
}

"use client";

import { useEffect, useState } from "react";
import { useRouter } from "next/navigation";
import { Search, MessageSquare, Eye } from "lucide-react";

import { Complaint } from "@/types";
import { complaintAPI } from "@/lib/api";
import { isAdmin } from "@/lib/auth";
import { Button } from "@/components/ui/Button";
import { Input } from "@/components/ui/Input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/Card";
import { formatDateTime, getStatusColor } from "@/lib/utils";

export default function MyComplaintsPage() {
    const router = useRouter();
    const [complaints, setComplaints] = useState<Complaint[]>([]);
    const [filteredComplaints, setFilteredComplaints] = useState<Complaint[]>([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // Redirect admins to dashboard
        if (isAdmin()) {
            router.push("/dashboard");
            return;
        }

        // Only fetch if not admin
        fetchComplaints();
    }, [router]);

    useEffect(() => {
        if (searchQuery.trim()) {
            const filtered = complaints.filter(
                (c) =>
                    c.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                    c.trackingId.toLowerCase().includes(searchQuery.toLowerCase())
            );
            setFilteredComplaints(filtered);
        } else {
            setFilteredComplaints(complaints);
        }
    }, [searchQuery, complaints]);

    const fetchComplaints = async () => {
        try {
            const response = await complaintAPI.getMyComplaints();
            if (response.data.success) {
                setComplaints(response.data.data.complaints);
                setFilteredComplaints(response.data.data.complaints);
            }
        } catch (error) {
            console.error("Failed to fetch complaints:", error);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="max-w-6xl mx-auto space-y-6">
            <div>
                <h1 className="text-3xl font-bold text-gray-900 mb-2">My Complaints</h1>
                <p className="text-gray-600">
                    View and track all your submitted complaints
                </p>
            </div>

            {/* Search */}
            <div className="relative">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
                <Input
                    placeholder="Search by title or tracking ID..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="pl-10"
                />
            </div>

            {/* Complaints List */}
            {isLoading ? (
                <div className="flex justify-center py-12">
                    <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
                </div>
            ) : filteredComplaints.length === 0 ? (
                <Card>
                    <CardContent className="py-12 text-center">
                        <p className="text-gray-600">
                            {searchQuery ? "No complaints found matching your search." : "You haven't submitted any complaints yet."}
                        </p>
                        {!searchQuery && (
                            <Button
                                onClick={() => router.push("/submit-complaint")}
                                className="mt-4"
                            >
                                Submit Your First Complaint
                            </Button>
                        )}
                    </CardContent>
                </Card>
            ) : (
                <div className="space-y-4">
                    {filteredComplaints.map((complaint) => (
                        <Card key={complaint._id} className="hover:shadow-md transition-shadow">
                            <CardContent className="p-6">
                                <div className="flex items-start justify-between gap-4">
                                    <div className="flex-1 min-w-0">
                                        <div className="flex items-center gap-3 mb-2">
                                            <h3 className="text-lg font-semibold text-gray-900 truncate">
                                                {complaint.title}
                                            </h3>
                                            <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium border whitespace-nowrap ${getStatusColor(complaint.status)}`}>
                                                {complaint.status.replace("-", " ")}
                                            </span>
                                        </div>

                                        <p className="text-sm text-gray-600 line-clamp-2 mb-3">
                                            {complaint.description}
                                        </p>

                                        <div className="flex flex-wrap items-center gap-4 text-sm text-gray-600">
                                            <div>
                                                <span className="font-medium">Tracking ID:</span>{" "}
                                                <code className="text-blue-600 font-mono">{complaint.trackingId}</code>
                                            </div>
                                            <div>
                                                <span className="font-medium">Category:</span>{" "}
                                                <span className="capitalize">{complaint.category.replace("-", " ")}</span>
                                            </div>
                                            <div>
                                                <span className="font-medium">Submitted:</span>{" "}
                                                {formatDateTime(complaint.createdAt)}
                                            </div>
                                        </div>
                                    </div>

                                    <div className="flex flex-col gap-2">
                                        <Button
                                            size="sm"
                                            onClick={() => router.push(`/track/${complaint.trackingId}`)}
                                        >
                                            <Eye className="w-4 h-4 mr-2" />
                                            View
                                        </Button>
                                        <Button
                                            size="sm"
                                            variant="outline"
                                            onClick={() => router.push(`/chat/${complaint.trackingId}`)}
                                        >
                                            <MessageSquare className="w-4 h-4 mr-2" />
                                            Chat
                                        </Button>
                                    </div>
                                </div>
                            </CardContent>
                        </Card>
                    ))}
                </div>
            )}
        </div>
    );
}

"use client";

import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useRouter } from "next/navigation";
import { toast } from "sonner";
import {
    FileText,
    AlertCircle,
    CheckCircle2,
    ArrowRight,
    ArrowLeft,
    Shield,
    Users,
    BookOpen,
    Building,
    Briefcase,
    HelpCircle
} from "lucide-react";

import { complaintSchema, type ComplaintFormData } from "@/schemas/complaint";
import { complaintAPI } from "@/lib/api";
import { Button } from "@/components/ui/Button";
import { Input } from "@/components/ui/Input";
import { Textarea } from "@/components/ui/Textarea";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/Card";

const categories = [
    { value: "harassment", label: "Harassment", icon: AlertCircle, color: "text-red-600 bg-red-50" },
    { value: "discrimination", label: "Discrimination", icon: Users, color: "text-orange-600 bg-orange-50" },
    { value: "academic-misconduct", label: "Academic Misconduct", icon: BookOpen, color: "text-purple-600 bg-purple-50" },
    { value: "infrastructure", label: "Infrastructure", icon: Building, color: "text-blue-600 bg-blue-50" },
    { value: "safety", label: "Safety", icon: Shield, color: "text-green-600 bg-green-50" },
    { value: "administration", label: "Administration", icon: Briefcase, color: "text-indigo-600 bg-indigo-50" },
    { value: "other", label: "Other", icon: HelpCircle, color: "text-gray-600 bg-gray-50" },
];

export default function SubmitComplaintPage() {
    const router = useRouter();
    const [step, setStep] = useState(1);
    const [isLoading, setIsLoading] = useState(false);
    const [trackingId, setTrackingId] = useState<string | null>(null);

    const {
        register,
        handleSubmit,
        watch,
        setValue,
        formState: { errors },
    } = useForm<ComplaintFormData>({
        resolver: zodResolver(complaintSchema),
    });

    const selectedCategory = watch("category");

    const onSubmit = async (data: ComplaintFormData) => {
        setIsLoading(true);

        try {
            const response = await complaintAPI.fileComplaint(data);

            if (response.data.success) {
                const { trackingId: id } = response.data.data;
                setTrackingId(id);
                setStep(4); // Success step
                toast.success("Complaint submitted successfully!");
            }
        } catch (error: any) {
            const message = error.response?.data?.message || "Failed to submit complaint. Please try again.";
            toast.error(message);
        } finally {
            setIsLoading(false);
        }
    };

    const nextStep = () => setStep((prev) => Math.min(prev + 1, 3));
    const prevStep = () => setStep((prev) => Math.max(prev - 1, 1));

    return (
        <div className="max-w-4xl mx-auto">
            <div className="mb-8">
                <h1 className="text-3xl font-bold text-gray-900 mb-2">Submit Anonymous Complaint</h1>
                <p className="text-gray-600">
                    Your identity is protected. Only use your tracking ID to check status.
                </p>
            </div>

            {/* Progress Steps */}
            {/* Progress Steps */}
            <div className="mb-12">
                <div className="flex items-center">
                    {[1, 2, 3].map((num) => (
                        <div key={num} className={`flex items-center ${num === 3 ? "" : "flex-1"}`}>
                            <div className="relative flex flex-col items-center">
                                <div
                                    className={`flex items-center justify-center w-10 h-10 rounded-full border-2 transition-colors z-10 ${step >= num
                                        ? "bg-blue-600 border-blue-600 text-white"
                                        : "bg-white border-gray-300 text-gray-500 font-medium"
                                        }`}
                                >
                                    {step > num ? <CheckCircle2 className="w-5 h-5" /> : num}
                                </div>
                                <div className="absolute top-12 whitespace-nowrap text-xs font-bold text-gray-900">
                                    {num === 1 && "Details"}
                                    {num === 2 && "Category"}
                                    {num === 3 && "Review"}
                                </div>
                            </div>
                            {num < 3 && (
                                <div
                                    className={`flex-1 h-1 mx-2 transition-colors ${step > num ? "bg-blue-600" : "bg-gray-200"
                                        }`}
                                />
                            )}
                        </div>
                    ))}
                </div>
            </div>

            <form onSubmit={handleSubmit(onSubmit)}>
                {/* Step 1: Complaint Details */}
                {step === 1 && (
                    <Card>
                        <CardHeader>
                            <CardTitle>Complaint Details</CardTitle>
                            <CardDescription>
                                Provide a clear title and detailed description of your grievance
                            </CardDescription>
                        </CardHeader>
                        <CardContent className="space-y-4">
                            <Input
                                label="Title"
                                placeholder="Brief summary of your complaint"
                                error={errors.title?.message}
                                {...register("title")}
                                required
                            />

                            <Textarea
                                label="Description"
                                placeholder="Provide detailed information about the incident, including dates, locations, and any relevant context..."
                                rows={8}
                                error={errors.description?.message}
                                {...register("description")}
                                required
                            />

                            <div className="flex justify-end">
                                <Button type="button" onClick={nextStep}>
                                    Next <ArrowRight className="w-4 h-4 ml-2" />
                                </Button>
                            </div>
                        </CardContent>
                    </Card>
                )}

                {/* Step 2: Category Selection */}
                {step === 2 && (
                    <Card>
                        <CardHeader>
                            <CardTitle>Select Category</CardTitle>
                            <CardDescription>
                                Choose the category that best describes your complaint
                            </CardDescription>
                        </CardHeader>
                        <CardContent className="space-y-4">
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                {categories.map((category) => (
                                    <button
                                        key={category.value}
                                        type="button"
                                        onClick={() => setValue("category", category.value as any)}
                                        className={`flex items-center gap-4 p-4 rounded-lg border-2 transition-all ${selectedCategory === category.value
                                            ? "border-blue-600 bg-blue-50"
                                            : "border-gray-200 hover:border-gray-300"
                                            }`}
                                    >
                                        <div className={`p-3 rounded-lg ${category.color}`}>
                                            <category.icon className="w-6 h-6" />
                                        </div>
                                        <div className="text-left">
                                            <p className="font-medium text-gray-900">{category.label}</p>
                                        </div>
                                    </button>
                                ))}
                            </div>
                            {errors.category && (
                                <p className="text-sm text-red-600">{errors.category.message}</p>
                            )}

                            <div className="flex justify-between">
                                <Button type="button" variant="outline" onClick={prevStep}>
                                    <ArrowLeft className="w-4 h-4 mr-2" /> Back
                                </Button>
                                <Button type="button" onClick={nextStep}>
                                    Next <ArrowRight className="w-4 h-4 ml-2" />
                                </Button>
                            </div>
                        </CardContent>
                    </Card>
                )}

                {/* Step 3: Review & Submit */}
                {step === 3 && (
                    <Card>
                        <CardHeader>
                            <CardTitle>Review & Submit</CardTitle>
                            <CardDescription>
                                Please review your complaint before submitting
                            </CardDescription>
                        </CardHeader>
                        <CardContent className="space-y-6">
                            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                                <div className="flex items-start gap-3">
                                    <Shield className="w-5 h-5 text-blue-600 mt-0.5" />
                                    <div>
                                        <h4 className="font-medium text-blue-900 mb-1">Anonymous Submission</h4>
                                        <p className="text-sm text-blue-700">
                                            Your identity will not be stored with this complaint. You will receive a unique tracking ID to check the status.
                                        </p>
                                    </div>
                                </div>
                            </div>

                            <div className="space-y-4">
                                <div>
                                    <label className="text-sm font-medium text-gray-900">Title</label>
                                    <p className="mt-1 text-gray-900">{watch("title")}</p>
                                </div>

                                <div>
                                    <label className="text-sm font-medium text-gray-900">Category</label>
                                    <p className="mt-1 text-gray-900">
                                        {categories.find((c) => c.value === selectedCategory)?.label}
                                    </p>
                                </div>

                                <div>
                                    <label className="text-sm font-medium text-gray-900">Description</label>
                                    <p className="mt-1 text-gray-900 whitespace-pre-wrap">{watch("description")}</p>
                                </div>
                            </div>

                            <div className="flex justify-between">
                                <Button type="button" variant="outline" onClick={prevStep}>
                                    <ArrowLeft className="w-4 h-4 mr-2" /> Back
                                </Button>
                                <Button type="submit" isLoading={isLoading}>
                                    {isLoading ? "Submitting..." : "Submit Complaint"}
                                </Button>
                            </div>
                        </CardContent>
                    </Card>
                )}

                {/* Step 4: Success */}
                {step === 4 && trackingId && (
                    <Card>
                        <CardContent className="py-12">
                            <div className="text-center space-y-6">
                                <div className="flex justify-center">
                                    <div className="p-4 bg-green-100 rounded-full">
                                        <CheckCircle2 className="w-16 h-16 text-green-600" />
                                    </div>
                                </div>

                                <div>
                                    <h2 className="text-2xl font-bold text-gray-900 mb-2">
                                        Complaint Submitted Successfully!
                                    </h2>
                                    <p className="text-gray-600">
                                        Your complaint has been filed anonymously. Save your tracking ID below.
                                    </p>
                                </div>

                                <div className="bg-gray-50 border-2 border-dashed border-gray-300 rounded-lg p-6">
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Your Tracking ID
                                    </label>
                                    <div className="flex items-center justify-center gap-4">
                                        <code className="text-2xl font-mono font-bold text-blue-600">
                                            {trackingId}
                                        </code>
                                        <Button
                                            type="button"
                                            size="sm"
                                            onClick={() => {
                                                navigator.clipboard.writeText(trackingId);
                                                toast.success("Tracking ID copied!");
                                            }}
                                        >
                                            Copy
                                        </Button>
                                    </div>
                                    <p className="text-xs text-gray-500 mt-2">
                                        Save this ID to track your complaint status
                                    </p>
                                </div>

                                <div className="flex gap-4 justify-center">
                                    <Button
                                        type="button"
                                        variant="outline"
                                        onClick={() => router.push("/my-complaints")}
                                    >
                                        View My Complaints
                                    </Button>
                                    <Button
                                        type="button"
                                        onClick={() => router.push(`/track/${trackingId}`)}
                                    >
                                        Track This Complaint
                                    </Button>
                                </div>
                            </div>
                        </CardContent>
                    </Card>
                )}
            </form>
        </div>
    );
}

"use client";

import { useEffect, useState } from "react";
import { useParams, useRouter } from "next/navigation";
import { toast } from "sonner";
import {
    Clock,
    CheckCircle2,
    AlertCircle,
    MessageSquare,
    ArrowLeft
} from "lucide-react";

import { Complaint } from "@/types";
import { complaintAPI } from "@/lib/api";
import { Button } from "@/components/ui/Button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/Card";
import { formatDateTime, getStatusColor } from "@/lib/utils";

export default function TrackComplaintPage() {
    const params = useParams();
    const router = useRouter();
    const trackingId = params.trackingId as string;

    const [complaint, setComplaint] = useState<Complaint | null>(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        fetchComplaint();
    }, [trackingId]);

    const fetchComplaint = async () => {
        try {
            const response = await complaintAPI.trackComplaint(trackingId);

            if (response.data.success) {
                setComplaint(response.data.data.complaint);
            }
        } catch (error: any) {
            const message = error.response?.data?.message || "Failed to fetch complaint";
            toast.error(message);
        } finally {
            setIsLoading(false);
        }
    };

    if (isLoading) {
        return (
            <div className="flex items-center justify-center min-h-[400px]">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
            </div>
        );
    }

    if (!complaint) {
        return (
            <div className="max-w-2xl mx-auto text-center py-12">
                <AlertCircle className="w-16 h-16 text-red-500 mx-auto mb-4" />
                <h2 className="text-2xl font-bold text-gray-900 mb-2">Complaint Not Found</h2>
                <p className="text-gray-600 mb-6">
                    The tracking ID you provided does not match any complaint.
                </p>
                <Button onClick={() => router.push("/my-complaints")}>
                    <ArrowLeft className="w-4 h-4 mr-2" />
                    Back to My Complaints
                </Button>
            </div>
        );
    }

    return (
        <div className="max-w-4xl mx-auto space-y-6">
            <div className="flex items-center justify-between">
                <div>
                    <h1 className="text-3xl font-bold text-gray-900 mb-2">Track Complaint</h1>
                    <p className="text-gray-600">Tracking ID: <code className="font-mono font-semibold text-blue-600">{trackingId}</code></p>
                </div>
                <Button onClick={() => router.push(`/chat/${trackingId}`)}>
                    <MessageSquare className="w-4 h-4 mr-2" />
                    Open Chat
                </Button>
            </div>

            {/* Status Card */}
            <Card>
                <CardHeader>
                    <CardTitle>Current Status</CardTitle>
                </CardHeader>
                <CardContent>
                    <div className="flex items-center justify-between">
                        <div>
                            <span className={`inline-flex items-center px-4 py-2 rounded-full text-sm font-medium border ${getStatusColor(complaint.status)}`}>
                                {complaint.status.replace("-", " ").toUpperCase()}
                            </span>
                        </div>
                        <div className="text-right">
                            <p className="text-sm text-gray-500">Priority</p>
                            <p className="font-medium text-gray-900 capitalize">{complaint.priority}</p>
                        </div>
                    </div>
                </CardContent>
            </Card>

            {/* Complaint Details */}
            <Card>
                <CardHeader>
                    <CardTitle>Complaint Details</CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                    <div>
                        <label className="text-sm font-medium text-gray-900">Title</label>
                        <p className="mt-1 text-gray-900">{complaint.title}</p>
                    </div>

                    <div>
                        <label className="text-sm font-medium text-gray-900">Category</label>
                        <p className="mt-1 text-gray-900 capitalize">{complaint.category.replace("-", " ")}</p>
                    </div>

                    <div>
                        <label className="text-sm font-medium text-gray-900">Description</label>
                        <p className="mt-1 text-gray-900 whitespace-pre-wrap">{complaint.description}</p>
                    </div>

                    <div className="grid grid-cols-2 gap-4 pt-4 border-t">
                        <div>
                            <label className="text-sm font-medium text-gray-900">Submitted</label>
                            <p className="mt-1 text-gray-900">{formatDateTime(complaint.createdAt)}</p>
                        </div>
                        <div>
                            <label className="text-sm font-medium text-gray-900">Last Updated</label>
                            <p className="mt-1 text-gray-900">{formatDateTime(complaint.updatedAt)}</p>
                        </div>
                    </div>
                </CardContent>
            </Card>

            {/* Status Timeline */}
            <Card>
                <CardHeader>
                    <CardTitle>Status History</CardTitle>
                </CardHeader>
                <CardContent>
                    <div className="space-y-4">
                        {complaint.statusHistory.length > 0 ? (
                            complaint.statusHistory.map((history, index) => (
                                <div key={index} className="flex gap-4">
                                    <div className="flex flex-col items-center">
                                        <div className="p-2 bg-blue-100 rounded-full">
                                            <CheckCircle2 className="w-4 h-4 text-blue-600" />
                                        </div>
                                        {index < complaint.statusHistory.length - 1 && (
                                            <div className="w-0.5 h-full bg-gray-200 my-1"></div>
                                        )}
                                    </div>
                                    <div className="flex-1 pb-4">
                                        <div className="flex items-center gap-2 mb-1">
                                            <span className="font-medium text-gray-900 capitalize">
                                                {history.status.replace("-", " ")}
                                            </span>
                                            <span className="text-sm text-gray-500">
                                                {formatDateTime(history.changedAt)}
                                            </span>
                                        </div>
                                        {history.comment && (
                                            <p className="text-sm text-gray-600">{history.comment}</p>
                                        )}
                                    </div>
                                </div>
                            ))
                        ) : (
                            <div className="text-center py-8 text-gray-500">
                                <Clock className="w-12 h-12 mx-auto mb-2 opacity-50" />
                                <p>No status updates yet</p>
                            </div>
                        )}
                    </div>
                </CardContent>
            </Card>
        </div>
    );
}

"use client";

import RoleGuard from "@/components/auth/RoleGuard";
import { LayoutDashboard, Building2, Users, Settings, LogOut } from "lucide-react";
import { logout } from "@/lib/auth";

export default function SuperAdminLayout({
    children,
}: {
    children: React.ReactNode;
}) {
    const navigation = [
        { name: "Platform Overview", href: "/super-admin", icon: LayoutDashboard },
        { name: "Organizations", href: "/super-admin/organizations", icon: Building2 },
        { name: "System Logs", href: "/super-admin/logs", icon: Settings },
    ];

    return (
        <RoleGuard allowedRoles={["super-admin"]}>
            <div className="min-h-screen bg-gray-100 flex">
                {/* Sidebar would go here - simplified for this file */}
                <aside className="w-64 bg-slate-900 text-white fixed inset-y-0">
                    <div className="p-6">
