import axios from "axios";

/**
 * API CLIENT FOR VOISAFE BACKEND
 * 
 * Features:
 * - Automatic JWT token attachment
 * - Request/response interceptors
 * - Error handling
 */

const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:5000/api";

// Create axios instance
const api = axios.create({
    baseURL: API_URL,
    headers: {
        "Content-Type": "application/json",
    },
    withCredentials: true, // Send cookies with requests
});

// Request interceptor - Add JWT token
api.interceptors.request.use(
    (config) => {
        // Get token from localStorage
        const token = localStorage.getItem("token");

        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }

        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);

// Response interceptor - Handle errors
api.interceptors.response.use(
    (response) => {
        return response;
    },
    (error) => {
        // Handle 401 Unauthorized - redirect to login
        if (error.response?.status === 401) {
            localStorage.removeItem("token");
            localStorage.removeItem("user");

            // Only redirect if not already on auth pages
            if (typeof window !== "undefined" && !window.location.pathname.startsWith("/login")) {
                window.location.href = "/login";
            }
        }

        return Promise.reject(error);
    }
);

export default api;

// API helper functions

/**
 * Authentication
 */
export const authAPI = {
    login: (email: string, password: string) =>
        api.post("/auth/login", { email, password }),

    register: (data: any) =>
        api.post("/auth/register", data),

    getMe: () =>
        api.get("/auth/me"),
};

/**
 * Complaints
 */
export const complaintAPI = {
    // Student endpoints
    fileComplaint: (data: any) =>
        api.post("/complaints", data),

    getMyComplaints: () =>
        api.get("/complaints/my-complaints"),

    trackComplaint: (trackingId: string) =>
        api.get(`/complaints/track/${trackingId}`),

    // Admin endpoints (if needed)
    getAllComplaints: (params?: any) =>
        api.get("/complaints", { params }),

    getComplaintById: (id: string) =>
        api.get(`/complaints/${id}`),

    updateStatus: (id: string, status: string, comment?: string) =>
        api.put(`/complaints/${id}/status`, { status, comment }),
};

import { User } from "@/types";

/**
 * AUTH HELPER FUNCTIONS
 * 
 * Client-side authentication utilities
 */

/**
 * Save auth data to localStorage
 */
export function saveAuth(token: string, user: User, organization?: any): void {
    if (typeof window === "undefined") return;

    localStorage.setItem("token", token);
    localStorage.setItem("user", JSON.stringify(user));

    if (organization) {
        localStorage.setItem("organization", JSON.stringify(organization));
    } else {
        localStorage.removeItem("organization");
    }
}

/**
 * Get current organization from localStorage
 */
export function getOrganization(): any | null {
    if (typeof window === "undefined") return null;

    const orgStr = localStorage.getItem("organization");
    if (!orgStr) return null;

    try {
        return JSON.parse(orgStr);
    } catch {
        return null;
    }
}

/**
 * Get current user from localStorage
 */
export function getUser(): User | null {
    if (typeof window === "undefined") return null;

    const userStr = localStorage.getItem("user");
    if (!userStr) return null;

    try {
        return JSON.parse(userStr);
    } catch {
        return null;
    }
}

/**
 * Get auth token from localStorage
 */
export function getToken(): string | null {
    if (typeof window === "undefined") return null;

    return localStorage.getItem("token");
}

/**
 * Check if user is authenticated
 */
export function isAuthenticated(): boolean {
    return !!getToken();
}

/**
 * Logout user
 */
export function logout(): void {
    if (typeof window === "undefined") return;

    localStorage.removeItem("token");
    localStorage.removeItem("user");
    localStorage.removeItem("organization");

    // Redirect to login
    window.location.href = "/login";
}

/**
 * Check if user has specific role
 */
export function hasRole(role: string | string[]): boolean {
    const user = getUser();
    if (!user) return false;

    if (Array.isArray(role)) {
        return role.includes(user.role);
    }

    return user.role === role;
}

/**
 * Check if user is student
 */
export function isStudent(): boolean {
    return hasRole("student");
}

/**
 * Check if user is admin
 */
export function isAdmin(): boolean {
    return hasRole(["admin", "committee-admin"]);
}

import { io, Socket } from "socket.io-client";

/**
 * SOCKET.IO CLIENT FOR ANONYMOUS CHAT
 * 
 * Features:
 * - JWT authentication
 * - Automatic reconnection
 * - Event handlers for chat
 */

const SOCKET_URL = process.env.NEXT_PUBLIC_SOCKET_URL || "http://localhost:5000";

let socket: Socket | null = null;

/**
 * Initialize Socket.io connection
 */
export const initializeSocket = (): Socket => {
    if (socket && socket.connected) {
        return socket;
    }

    // Get token from localStorage
    const token = localStorage.getItem("token");

    if (!token) {
        throw new Error("No authentication token found");
    }

    // Create socket connection with auth
    socket = io(SOCKET_URL, {
        auth: {
            token,
        },
        reconnection: true,
        reconnectionAttempts: 5,
        reconnectionDelay: 1000,
    });

    // Connection event handlers
    socket.on("connect", () => {
        console.log("✅ Socket connected:", socket?.id);
    });

    socket.on("disconnect", (reason) => {
        console.log("🔌 Socket disconnected:", reason);
    });

    socket.on("connect_error", (error) => {
        console.error("❌ Socket connection error:", error);
    });

    return socket;
};

/**
 * Get existing socket instance
 */
export const getSocket = (): Socket | null => {
    return socket;
};

/**
 * Disconnect socket
 */
export const disconnectSocket = (): void => {
    if (socket) {
        socket.disconnect();
        socket = null;
    }
};

/**
 * Join chat room for a complaint
 */
export const joinChatRoom = (trackingId: string): void => {
    if (!socket) {
        throw new Error("Socket not initialized");
    }

    socket.emit("join_chat", { trackingId });
};

/**
 * Leave chat room
 */
export const leaveChatRoom = (trackingId: string): void => {
    if (!socket) return;

    socket.emit("leave_chat", { trackingId });
};

/**
 * Send message
 */
export const sendMessage = (trackingId: string, message: string): void => {
    if (!socket) {
        throw new Error("Socket not initialized");
    }

    socket.emit("send_message", { trackingId, message });
};

/**
 * Send typing indicator
 */
export const sendTyping = (trackingId: string, isTyping: boolean): void => {
    if (!socket) return;

    socket.emit("typing", { trackingId, isTyping });
};

/**
 * Socket event listeners
 */
export const socketEvents = {
    onChatHistory: (callback: (data: any) => void) => {
        socket?.on("chat_history", callback);
    },

    onMessageReceived: (callback: (data: any) => void) => {
        socket?.on("message_received", callback);
    },

    onUserTyping: (callback: (data: any) => void) => {
        socket?.on("user_typing", callback);
    },

    onUserJoined: (callback: (data: any) => void) => {
        socket?.on("user_joined", callback);
    },

    onUserLeft: (callback: (data: any) => void) => {
        socket?.on("user_left", callback);
    },

    onError: (callback: (data: any) => void) => {
        socket?.on("error", callback);
    },

    // Remove listeners
    off: (event: string, callback?: any) => {
        socket?.off(event, callback);
    },
};

import { NextResponse } from "next/server";
import type { NextRequest } from "next/server";

/**
 * MIDDLEWARE FOR ROUTE PROTECTION
 * 
 * Note: Since we're using localStorage for tokens (client-side),
 * we can't validate auth in middleware (server-side).
 * Auth protection is handled by client-side redirects in pages.
 */

export function middleware(request: NextRequest) {
    const { pathname } = request.nextUrl;

    // Allow all routes - auth is handled client-side
    // This is because tokens are in localStorage, not cookies
    return NextResponse.next();
}

// Configure which routes to run middleware on
export const config = {
    matcher: [
        /*
         * Match all request paths except for the ones starting with:
         * - api (API routes)
         * - _next/static (static files)
         * - _next/image (image optimization files)
         * - favicon.ico (favicon file)
         */
        "/((?!api|_next/static|_next/image|favicon.ico).*)",
    ],
};

/**
 * TYPESCRIPT TYPES FOR VOISAFE FRONTEND
 */

export interface User {
    _id: string;
    name: string;
    email: string;
    role: "student" | "admin" | "committee-admin" | "super-admin";
    college: string;
    studentId?: string;
    department?: string;
    year?: number;
    isActive: boolean;
    isVerified: boolean;
    createdAt: string;
    lastLogin?: string;
}

export interface Complaint {
    _id: string;
    trackingId: string;
    title: string;
    description: string;
    category: ComplaintCategory;
    priority: "low" | "medium" | "high" | "urgent";
    status: ComplaintStatus;
    college: string;
    attachments?: Attachment[];
    adminNotes?: AdminNote[];
    statusHistory: StatusHistory[];
    identityRevealed: boolean;
    identityRevealedBy?: string;
    identityRevealedAt?: string;
    createdAt: string;
    updatedAt: string;
}

export type ComplaintCategory =
    | "harassment"
    | "discrimination"
    | "academic-misconduct"
    | "infrastructure"
    | "safety"
    | "administration"
    | "other";

export type ComplaintStatus =
    | "pending"
    | "under-review"
    | "in-progress"
    | "resolved"
    | "closed";

export interface Attachment {
    filename: string;
    url: string;
    uploadedAt: string;
}

export interface AdminNote {
    note: string;
    addedBy: string;
    addedAt: string;
}

export interface StatusHistory {
    status: ComplaintStatus;
    changedBy: string;
    changedAt: string;
    comment?: string;
}

export interface ChatMessage {
    _id: string;
    trackingId: string;
    senderRole: "student" | "admin" | "committee-admin" | "system";
    adminId?: string;
    message: string;
    messageType: "text" | "file" | "system";
    attachment?: {
        filename: string;
        url: string;
        fileType: string;
        fileSize: number;
    };
    isRead: boolean;
    readAt?: string;
    createdAt: string;
    senderName?: string; // Only for admin messages
}

export interface ComplaintFormData {
    title: string;
    description: string;
    category: ComplaintCategory;
}

export interface LoginFormData {
    email: string;
    password: string;
}

export interface RegisterFormData {
    name: string;
    email: string;
    password: string;
    confirmPassword: string;
    college: string;
    studentId: string;
    department: string;
    year: number;
}

export interface ApiResponse<T = any> {
    success: boolean;
    message?: string;
    data?: T;
    count?: number;
}

export interface ApiError {
    success: false;
    message: string;
    error?: string;
}
